const unsigned* PropDescImpl::getPropVals(unsigned propId, unsigned& num) // should include XXX_VAL_INVALID for invalid values (non-brig only)
{
    switch(propId)
    {
    case PROP_ALIGN: 	num = sizeof(PROP_VALUES_ALIGN) / sizeof(unsigned); return PROP_VALUES_ALIGN;

    case PROP_SIGNALOPERATION:
    case PROP_ATOMICOPERATION: 	num = sizeof(PROP_VALUES_ATMOP) / sizeof(unsigned); return PROP_VALUES_ATMOP;

    case PROP_COMPARE: 	num = sizeof(PROP_VALUES_COMPARE) / sizeof(unsigned); return PROP_VALUES_COMPARE;

    case PROP_ISCONST: 	num = sizeof(PROP_VALUES_CONST) / sizeof(unsigned); return PROP_VALUES_CONST;

    case PROP_EQUIVCLASS: 	num = sizeof(PROP_VALUES_EQCLASS) / sizeof(unsigned); return PROP_VALUES_EQCLASS;

    case PROP_FTZ: 	num = sizeof(PROP_VALUES_FTZ) / sizeof(unsigned); return PROP_VALUES_FTZ;

    case PROP_GEOMETRY: 	num = sizeof(PROP_VALUES_GEOMETRY) / sizeof(unsigned); return PROP_VALUES_GEOMETRY;

    case PROP_IMAGEQUERY: 	num = sizeof(PROP_VALUES_IPROP) / sizeof(unsigned); return PROP_VALUES_IPROP;

    case PROP_MEMORYORDER: 	num = sizeof(PROP_VALUES_MEMORD) / sizeof(unsigned); return PROP_VALUES_MEMORD;

    case PROP_GLOBALSEGMENTMEMORYSCOPE:
    case PROP_GROUPSEGMENTMEMORYSCOPE:
    case PROP_IMAGESEGMENTMEMORYSCOPE:
    case PROP_MEMORYSCOPE: 	num = sizeof(PROP_VALUES_MEMSCP) / sizeof(unsigned); return PROP_VALUES_MEMSCP;

    case PROP_ISNONULL: 	num = sizeof(PROP_VALUES_NONULL) / sizeof(unsigned); return PROP_VALUES_NONULL;

    case PROP_D0:
    case PROP_D1:
    case PROP_S0:
    case PROP_S1:
    case PROP_S2:
    case PROP_S3:
    case PROP_S4:
    case PROP_OPERAND: 	num = sizeof(PROP_VALUES_OPERAND) / sizeof(unsigned); return PROP_VALUES_OPERAND;

    case PROP_PACK: 	num = sizeof(PROP_VALUES_PACK) / sizeof(unsigned); return PROP_VALUES_PACK;

    case PROP_ROUND: 	num = sizeof(PROP_VALUES_ROUND) / sizeof(unsigned); return PROP_VALUES_ROUND;

    case PROP_SEGMENT: 	num = sizeof(PROP_VALUES_SEGMENT) / sizeof(unsigned); return PROP_VALUES_SEGMENT;

    case PROP_SAMPLERQUERY: 	num = sizeof(PROP_VALUES_SPROP) / sizeof(unsigned); return PROP_VALUES_SPROP;

    case PROP_STYPESIZE: 	num = sizeof(PROP_VALUES_STYPESIZE) / sizeof(unsigned); return PROP_VALUES_STYPESIZE;

    case PROP_COORDTYPE:
    case PROP_IMAGETYPE:
    case PROP_SIGNALTYPE:
    case PROP_SOURCETYPE:
    case PROP_TYPE: 	num = sizeof(PROP_VALUES_TYPE) / sizeof(unsigned); return PROP_VALUES_TYPE;

    case PROP_TYPESIZE: 	num = sizeof(PROP_VALUES_TYPESIZE) / sizeof(unsigned); return PROP_VALUES_TYPESIZE;

    case PROP_WIDTH: 	num = sizeof(PROP_VALUES_WIDTH) / sizeof(unsigned); return PROP_VALUES_WIDTH;

    }
    assert(false);
    num = 0;
    return 0;
}

