        if (!check_type_values_sig32(getSignalType<T>(inst))) return false;
        if (!validateOperand(inst, PROP_S1, OPERAND_ATTR_SIG32, OPERAND_VALUES_REG, sizeof(OPERAND_VALUES_REG) / sizeof(unsigned), false)) return false;
